var Layout = (function () {
  var n = function () {
    $(window).scrollTop() > 60
      ? $("body").addClass("page-on-scroll")
      : $("body").removeClass("page-on-scroll");
  };
  return {
    init: function () {
      n(),
        $(window).scroll(function () {
          n();
        });
    },
    getViewPort: function () {
      var n = window,
        o = "inner";
      return (
        "innerWidth" in window ||
          ((o = "client"), (n = document.documentElement || document.body)),
        { width: n[o + "Width"], height: n[o + "Height"] }
      );
    },
  };
})();
$(document).ready(function () {
  Layout.init();
});


const sidebarToggle = document.getElementById('sidebar-toggle');
const demoRemote = document.getElementById('demo-remote');

sidebarToggle.addEventListener('click', () => {
    demoRemote.classList.toggle('active'); // Show/hide sidebar
    sidebarToggle.classList.toggle('active'); // Move the arrow with sidebar
    
    // Change the arrow direction
    const arrowIcon = sidebarToggle.querySelector('i');
    if (demoRemote.classList.contains('active')) {
        arrowIcon.innerHTML = '&#x25C4;'; // Left-pointing arrow when open
    } else {
        arrowIcon.innerHTML = '&#x25BA;'; // Right-pointing arrow when closed
    }
});

